import { useState, useEffect } from 'react';
import './App.css';

import Header from "./components/header/header";
import MainContent from "./components/maincontent/MainContent";

const arrayBlook = [
    {
      nameItem: "кеды",
        model: "TomyHellFiger"

    },
    {
        nameItem: "кросы",
        model: "Адидас"
    },
    {
        nameItem: "кросовки",
        model: "Rebuk"
    }
]

function App() {
    const [inputValueSearch, setValue] = useState('');

    const [items, setItems] = useState([]);

    const  onItemSearch = (e) => {
        const { value }= e.target;
        setValue(value)

        const itemAfterFilter = arrayBlook.filter(({model}) => (
            model.toLowerCase().nameItem.toLowerCase().includes( value.toLowerCase())
            )
        );

        setItems(itemAfterFilter);
    }


    console.log(inputValueSearch);

    useEffect(()=>{
        setTimeout(() => setItems(arrayBlook), 2000)
    }, []);



  return (
    <div className="App">

        <Header
            onChangeValue={onItemSearch}
            inputValueSearch={inputValueSearch}
        />
        <MainContent arrayBlock={items}/>
        <div style = {{
            display: "flex",
            justifyContent: "center"
        }}>
        {
            items.length === 0 && ( <div className="loader-wrapper">
                <div className="lds-default">
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>
            </div>)
        }
        </div>

    </div>
  )
}

export default App;
